DROP TABLE IF EXISTS `content_ext_news`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `content_ext_news` (
  `nid` int(10) unsigned NOT NULL auto_increment,
  `title` varchar(255) NOT NULL default '' COMMENT '标题',
  `body` text COMMENT '内容',
  `uid` int(10) unsigned NOT NULL default '0' COMMENT '创建用户 id',
  `status` tinyint(4) NOT NULL default '1' COMMENT '状态, 0：隐藏，1、正常',
  `comment_count` int(10) unsigned NOT NULL default '0' COMMENT '评论数量',
  `format` int(10) unsigned NOT NULL default '0' COMMENT '输入格式',
  `visit` int(10) unsigned NOT NULL default '0' COMMENT '浏览计数',
  `created` int(10) unsigned NOT NULL default '0' COMMENT '创建时间',
  `updated` int(10) unsigned NOT NULL default '0' COMMENT '更新时间',
  `fid` int(11) NOT NULL default '0' COMMENT '图片id',
  `flag` varchar(12) NOT NULL default '0' COMMENT '推荐位',
  `description` mediumtext COMMENT 'description',
  `check_user` int(11) NOT NULL default '0' COMMENT '审核人id',
  `check_status` smallint(6) NOT NULL default '0' COMMENT '审核状态0不通过1通过',
  `check_time` int(11) NOT NULL default '0',
  PRIMARY KEY  (`nid`),
  KEY `sc` (`status`,`created`),
  KEY `su` (`status`,`updated`),
  KEY `scc` (`status`,`comment_count`),
  KEY `sv` (`status`,`visit`),
  KEY `uu` (`uid`,`updated`),
  KEY `uc` (`uid`,`comment_count`),
  KEY `uv` (`uid`,`visit`)
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

